/* eslint-disable no-unused-vars */
// eslint-disable-next-line no-unused-vars
import React from 'react';
import Wrapper from '../body/wrapper';
import Nav from '../navbar';
import Host from '../body/playHost';
import Play from '../body/playWindow';
import Images from '../../images.json';

class Container extends React.Component {
    state = {
        score: 0,
        topScore: 0,
        status: "Click An Image To Begin!",
        picsRandom: []
    };

componentDidMount() {
    this.randomizeImages();
}

randomizeImages = () => {
    var currentIndex = Images.length, temporaryValue, randomIndex;

    while (0 !== currentIndex) {

        randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex -= 1;

        temporaryValue = Images[currentIndex];
        Images[currentIndex] = Images[randomIndex];
        Images[randomIndex] = temporaryValue;
    }
    // let randomArray = [];

    // for (let i = 0; i < Images.length; i ++) {
    //     let randomize = Images[Math.floor(Math.random() * Images.length)];
    //     randomArray.push(randomize);
    // }

    this.setState({ picsRandom: Images }, () => {
        console.log(this.state.picsRandom);
    });

}

scoreIncrement = event => {
    const status = event.target.value
    console.log(status);
    this.setState({ score: this.state.score + 1 });
    this.randomizeImages();

}

    render() {
        return (
            <Wrapper>
                <Nav 
                score={this.state.score}
                topScore={this.state.topScore}
                status={this.state.status}
                />
                <Host>
                    {this.state.picsRandom.map(pic => {
                    return (
                      <Play 
                      key={pic.id}
                      id={pic.id}
                      src={pic.image}
                      value={pic.click}
                      onClick={() => this.scoreIncrement(pic.id)}
                      />  
                    );   
                    })}
                </Host>
            </Wrapper>
        );
    }
}

export default Container;
